package vn.edu.fit.iuh.repositories;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import vn.edu.fit.iuh.enities.Candidate;import java.util.List;@Repositorypublic interface CandidateRepository extends JpaRepository<Candidate, Long> {    @Query("SELECT c FROM Candidate c LEFT JOIN FETCH c.address")    List<Candidate> findAllCandidatesWithAddress();}